import React from 'react';
import { connect } from 'react-redux';
import { Button, TextField }  from '@material-ui/core';
import axios from 'axios';
import { ACTIONS, QUERY_ENDPOINT, COMMON_GREMLIN_ERROR } from '../../constants';
import { onFetchQuery } from '../../logics/actionHelper';

function sendQuery() {
    // this.props.dispatch({ type: ACTIONS.CLEAR_GRAPH });
    this.props.dispatch({ type: ACTIONS.SET_ERROR, payload: null });
    axios.post(
      QUERY_ENDPOINT,
      { host: this.props.host, port: this.props.port, query: this.props.query, nodeLimit: this.props.nodeLimit },
      { headers: { 'Content-Type': 'application/json' } }
    ).then((response) => {
      onFetchQuery(response, this.props.query, this.props.nodeLabels, this.props.dispatch);
    }).catch((error) => {
      this.props.dispatch({ type: ACTIONS.SET_ERROR, payload: COMMON_GREMLIN_ERROR });
    });
  }


export const HeaderComp = ({port, host, query, changeHost, changePort, changeQuery}) => {

    return (
        <header>
          <form>
            <TextField value={host} onChange={changeHost} label="host" style={{width: '10%'}} />
            <TextField value={port} onChange= {changePort}  label="port" style={{width: '10%'}} />
            <TextField value={query} onChange={changeQuery} label="gremlin query example: g.V()" style={{width: '50%'}} />
            <Button variant="contained" color="primary" style={{width: '150px'}}> Execute Query</Button>
          </form>
    </header>
      )
    }
export default HeaderComp
