// Sep file for the options of the network
export const optionsSettings = {
    nodeLabels: [],
    queryHistory: [],
    isPhysicsEnabled: false,
    nodeLimit: 10,
    networkOptions: {
      autoResize: false,
      // https://visjs.github.io/vis-network/docs/network/physics.html
      physics: {
        barnesHut: {
          avoidOverlap: 100
        },
        maxVelocity: 10,
        solver: 'barnesHut',
        timestep: 0.35,
        stabilization: {
          enabled: true,
          iterations: 50,
          updateInterval: 25
        }
      },
      //https://visjs.github.io/vis-network/docs/network/nodes.html
      nodes: {
        shape: "circle",
        size: 100,
        borderWidth: 2,
        font: {
          size: 11
        },
        color: 'red'
      },
      // https://visjs.github.io/vis-network/docs/network/edges.html
      edges: {
        width: 2,
        font: {
          size: 11
        }
      }
    }
  };
