import React from 'react'
import {connect} from 'react-redux';
import vis from 'vis-network';
import { ACTIONS } from '../../constants';
import Graph from "react-vis-network-graph";

export const NetworkField = ({response}) => {
  function randomColor() {
    const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
    const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
    const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
    return `#${red}${green}${blue}`;
  }
  const graph = {
    nodes: [
      { id: 1, label: response, title: "node 1 tootip text" },
      { id: 2, label: response, title: "node 2 tootip text" },
      { id: 3, label: response, title: "node 3 tootip text" },
      { id: 4, label: response, title: "node 4 tootip text" },
      { id: 5, label: response, title: "node 5 tootip text" }
    ],
    edges: [
      { from: 1, to: 2, label: response},
      { from: 1, to: 3 },
      { from: 2, to: 4 },
      { from: 2, to: 5 }
    ]
  }
  const options = {
    layout: {
      hierarchical: true
    },
    edges: {
      color: "#000000"
    },
    height: "500px"
  }

  const createNode = (x, y) => {
    const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      const from = Math.floor(Math.random() * (counter - 1)) + 1;
      return {
        graph: {
          nodes: [
            ...nodes,
            { id, label: `Node ${id}`, color, x, y }
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }

  const events = {
    select: function(event) {
      var { nodes, edges } = event},
      doubleClick: ({ pointer: { canvas } }) => {
        createNode(canvas.x, canvas.y)
      }
    }

return (
<div>
<Graph
  graph={graph}
  options={options}
  events={events} getNetwork={network => {
    //  if you want access to vis.js network api you can set the state in a parent component using this property
  }}>

  </Graph>
</div>
)
}
export default NetworkField
