import React from 'react';
import { connect } from 'react-redux';
import { Button, TextField }  from '@material-ui/core';
import axios from 'axios';
import { ACTIONS, QUERY_ENDPOINT, COMMON_GREMLIN_ERROR } from '../../constants';
import { onFetchQuery } from '../../logics/actionHelper';

export const HeaderComp = ({port, host, query, onChangeEvent}) => {

    return (
        <div className={'header'}>
          <form noValidate autoComplete="off">
            <TextField value={host} onChange={onChangeEvent}  label="host" style={{width: '10%'}} />
            <TextField value={port} onChange={onChangeEvent}  label="port" style={{width: '10%'}} />
            <TextField value={query} onChange={onChangeEvent} label="gremlin query example: g.V()" style={{width: '50%'}} />
            <Button variant="contained" color="primary" style={{width: '150px'}}> Execute Query</Button>
          </form>

          <br />
          <div style={{color: 'red'}}>{this.props.error}</div>
        </div>
      )
    }
export default HeaderComp
