import React, { useContext } from "react";
import { Context } from "../../Context";
// import { connect } from 'react-redux';
import { Button, TextField }  from '@material-ui/core';
import axios from 'axios';
import { ACTIONS, QUERY_ENDPOINT, COMMON_GREMLIN_ERROR } from '../../constants';
import { onFetchQuery } from '../../logics/actionHelper';
import  {useState} from 'react'
import { useDispatch, useSelector  } from "react-redux";

export const HeaderComp = ({port, host, query, changeHost, changePort, changeQuery}) => {
    // const count = useSelector(state => state.counter.count);
    // const dispatch = useDispatch();
    // const nodes = useSelector(state => state.nodes)
    const { nodes, setNodes } = useContext(Context);

    const sendQuery = () => {
        // this.props.dispatch({ type: ACTIONS.CLEAR_GRAPH });
        axios.post(
          QUERY_ENDPOINT,
          { host: host, port: port, query: query, nodeLimit: 4 },
          { headers: { 'Content-Type': 'application/json' } }
        ).then((response) => {
            // console.log(response)
            const gremlinResponse = onFetchQuery(response, query, [])
            setNodes((nodes) => gremlinResponse);
        }).catch((error) => {
        //   this.props.dispatch({ type: ACTIONS.SET_ERROR, payload: COMMON_GREMLIN_ERROR });
        // TODO: Handle the error condition maybe some warning    
        console.log(error)
        });
      }


    return (
        <header>
          <form>
            <TextField value={host} onChange={changeHost} label="host" style={{width: '10%'}} />
            <TextField value={port} onChange= {changePort}  label="port" style={{width: '10%'}} />
            <TextField value={query} onChange={changeQuery} label="gremlin query example: g.V()" style={{width: '50%'}} />
            <Button variant="contained" color="primary" style={{width: '150px'}} onClick={sendQuery} > Execute Query</Button>
          </form>
    </header>
      )
    }
export default HeaderComp
