import { connect } from 'react-redux';
import { Button, TextField }  from '@material-ui/core';
import axios from 'axios';
import { ACTIONS, QUERY_ENDPOINT, COMMON_GREMLIN_ERROR } from '../../constants';
import { onFetchQuery } from '../../logics/actionHelper';

export const HeaderComp = ({port, host, query, onChangeEvent, changePort}) => {

    return (
        <header>
          <form>
            <TextField value={host} onChange={changeHost} label="host" style={{width: '10%'}} />
            <TextField value={port} onChange= {changePort}  label="port" style={{width: '10%'}} />
            <TextField value={query} onChange={changeQuery} label="gremlin query example: g.V()" style={{width: '50%'}} />
            <Button variant="contained" color="primary" style={{width: '150px'}}> Execute Query</Button>
          </form>
    </header>
      )
    }
export default HeaderComp
